"use strict";window.onload=()=>{forum.init(),panel()};const forum={data:function(){fetch("assets/data/articles.json").then(t=>{t.json().then(t=>{this.articles=t})})},init:function(){this.data(),this.helpers(),setTimeout(()=>{this.catcheDOM(),this.bindEvents(),this.render()},500)},catcheDOM:function(){this.input=document.querySelector('.search-input input[type="search"]'),this.list=document.querySelector(".timeline-body")},bindEvents:function(){this.input.addEventListener("keyup",()=>{this.render()})},helpers:function(){$(".timeline").mCustomScrollbar({scrollInertia:350,autoHideScrollbar:!0,mouseWheel:{scrollAmount:200}}),$(".article").mCustomScrollbar({scrollInertia:350,autoHideScrollbar:!0,mouseWheel:{scrollAmount:200}})},render:function(){const t=this.articles.filter(t=>{const e=this.input.value||"";return t.title.toUpperCase().indexOf(e.toUpperCase())>-1});this.list.innerHTML="",t.length>0?t.forEach(t=>{const e='<article class="post"> <p class="post-title">'+t.title+"</p>"+'<div class="category"><div class="category-item snowboard"></div><div class="category-item ski"></div><div class="category-item skateboard"></div></div>'+('<div class="info"><div class="info-wrap"><p class="info__tag">'+t.tags+'</p><p class="info__date">'+t.date+'</p></div><div class="info-wrap"><a class="info__flame" href="#"><img src="assets/images/icon_flame.svg" class="mCS_img_loaded"></a><a class="info__user" href="#" style="background-image: url(\''+t.avatar+"');\"></a></div></div></article>");this.list.insertAdjacentHTML("beforeEnd",e)}):this.list.insertAdjacentHTML("beforeEnd","<p>Нетъ</p>")}},panel=()=>{const t=document.querySelector(".menu-user"),e=document.querySelector(".panel");t.addEventListener("click",()=>{e.classList.toggle("active")})};